  text: string;
}

import { GoogleGenAI, Type } from "@google/genai";
import { Product } from "../types";

const API_KEY = process.env.API_KEY || "";

export const getShoppingAdvice = async (userMessage: string, availableProducts: Product[]) => {
  const ai = new GoogleGenAI({ apiKey: API_KEY });

  const productContext = availableProducts.map(p => 
    `${p.name} (${p.brand}) - $${p.price}. Category: ${p.category}. Description: ${p.description}`
  ).join('\n');

  const systemPrompt = `
    You are a friendly and expert shopping assistant for "Raja Enterprises", a premium mobile and electronics store.
    Your goal is to help customers find the right products based on their needs, budget, and preferences.

    Available Inventory:
    ${productContext}

    Guidelines:
    1. Be polite, professional, and helpful.
    2. Recommend specific products from the inventory above when appropriate.
    3. If a product isn't in inventory, suggest the closest alternative or explain why we don't carry it.
    4. Keep answers concise but informative.
    5. Use markdown for better formatting.
  `;

  try {
    const response = await ai.models.generateContent({
      model: 'gemini-3-flash-preview',
      contents: userMessage,
      config: {
        systemInstruction: systemPrompt,
        temperature: 0.7,
      }
    });

    return response.text || "I'm sorry, I couldn't process that. How else can I help you today?";
  } catch (error) {
    console.error("Gemini API Error:", error);
    return "Our AI assistant is currently taking a coffee break. Please try again in a moment!";
  }
};

