        <span className="absolute right-full mr-3 bg-white text-slate-900 px-4 py-2 rounded-2xl text-xs font-bold shadow-xl border border-slate-100 opacity-0 group-hover:opacity-100 transition-opacity whitespace-nowrap">
          Order on WhatsApp
        </span>
      </a>

      <AIChatBot mode={userMode} />

      {isDashboardOpen && <B2BDashboard onClose={() => setIsDashboardOpen(false)} />}

      {isAdminPanelOpen && <AdminPanel onClose={() => setIsAdminPanelOpen(false)} />}

      {/* Product Detail Modal */}
      {selectedProduct && (
        <div className="fixed inset-0 z-[110] flex items-center justify-center p-4">
          <div className="absolute inset-0 bg-slate-900/80 backdrop-blur-md" onClick={() => setSelectedProduct(null)}></div>
          <div className="relative bg-white rounded-[3rem] shadow-2xl max-w-5xl w-full max-h-[90vh] overflow-y-auto flex flex-col lg:flex-row border border-white/20">
            <button 
              onClick={() => setSelectedProduct(null)}
              className="absolute top-6 right-6 p-3 bg-slate-100 rounded-full hover:bg-slate-200 transition-all z-10"
            >
              <X className="w-6 h-6" />
            </button>
            <div className="w-full lg:w-1/2 bg-slate-50 p-12 flex items-center justify-center">
              <img src={selectedProduct.image} alt={selectedProduct.name} className="w-full max-w-sm rounded-[2.5rem] shadow-2xl rotate-2" />
            </div>
            <div className="w-full lg:w-1/2 p-12">
              <span className="text-indigo-600 font-black uppercase tracking-[0.3em] text-[10px]">{selectedProduct.brand}</span>
              <h2 className="text-4xl font-black text-slate-900 mt-2 mb-6 tracking-tighter">{selectedProduct.name}</h2>
              <p className="text-slate-500 font-medium leading-relaxed mb-8">{selectedProduct.description}</p>

              <div className="space-y-6 mb-10">
                <h4 className="font-black text-slate-900 uppercase tracking-widest text-[10px]">Specifications</h4>
                <div className="grid grid-cols-2 gap-x-8 gap-y-4">
                  {Object.entries(selectedProduct.specs).map(([key, val]) => (
                    <div key={key} className="text-sm">
                      <p className="text-slate-400 font-bold uppercase text-[9px] mb-1">{key}</p>
                      <p className="text-slate-900 font-bold">{val}</p>
                    </div>
                  ))}
                </div>
              </div>

              <div className="flex flex-col sm:flex-row items-center justify-between pt-10 border-t border-slate-100 gap-6">
                <div>
                  <p className="text-[10px] font-black text-slate-400 uppercase mb-1">{userMode === 'wholesale' ? 'Wholesale Price (Per Unit)' : 'Best Buy Price'}</p>
                  <p className="text-4xl font-black text-slate-900 tracking-tighter">‚Çπ{(userMode === 'wholesale' ? selectedProduct.wholesalePrice : selectedProduct.retailPrice).toLocaleString()}</p>
                </div>
                <button 
                  onClick={() => { addToCart(selectedProduct); setSelectedProduct(null); }}
                  className="w-full sm:w-auto bg-indigo-600 text-white px-12 py-5 rounded-[2rem] font-black hover:bg-indigo-700 transition-all shadow-xl shadow-indigo-600/20 tracking-tight"
                >
                  {userMode === 'wholesale' ? `Add ${selectedProduct.moq} (Min. Qty)` : 'Add to Bag'}
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Shopping Cart Drawer */}
      {isCartOpen && (
        <div className="fixed inset-0 z-[120]">
          <div className="absolute inset-0 bg-slate-900/60 backdrop-blur-sm" onClick={() => setIsCartOpen(false)}></div>
          <div className="absolute right-0 top-0 bottom-0 w-full max-w-lg bg-white shadow-2xl flex flex-col animate-slide-in">
            <div className="p-8 border-b border-slate-100 flex justify-between items-center bg-white">
              <h2 className="text-2xl font-black text-slate-900 tracking-tighter flex items-center gap-3">
                Your Bag <span className="text-xs font-black text-indigo-600 bg-indigo-50 px-3 py-1 rounded-full uppercase">{cartCount} items</span>
              </h2>
              <button onClick={() => setIsCartOpen(false)} className="p-2 hover:bg-slate-100 rounded-full transition-colors">
                <X className="w-6 h-6 text-slate-400" />
              </button>
            </div>

            <div className="flex-1 overflow-y-auto p-8 space-y-8">
              {cart.length === 0 ? (
                <div className="h-full flex flex-col items-center justify-center text-slate-300 text-center">
                  <Smartphone className="w-20 h-20 mb-6 opacity-10" />
                  <p className="font-bold text-slate-400">Your bag is currently empty.</p>
                  <button onClick={() => setIsCartOpen(false)} className="mt-4 text-indigo-600 font-bold text-sm underline">Start Shopping</button>
                </div>
              ) : (
                cart.map(item => (
                  <div key={item.product.id} className="flex gap-6 group">
                    <div className="w-24 h-24 bg-slate-50 rounded-2xl overflow-hidden shrink-0 border border-slate-100">
                      <img src={item.product.image} className="w-full h-full object-cover" />
                    </div>
                    <div className="flex-1 flex flex-col justify-center">
                      <div className="flex justify-between items-start mb-1">
                        <h4 className="font-black text-slate-900 text-lg tracking-tight group-hover:text-indigo-600 transition-colors leading-none">{item.product.name}</h4>
                        <button onClick={() => removeFromCart(item.product.id)} className="text-slate-300 hover:text-red-500 transition-colors"><X className="w-4 h-4" /></button>
                      </div>
                      <p className="text-xs font-bold text-slate-400 uppercase tracking-widest mb-3">{item.product.brand}</p>
                      <div className="flex justify-between items-center mt-auto">
                        <span className="text-sm font-black text-slate-900 bg-slate-100 px-3 py-1 rounded-lg">Qty: {item.quantity}</span>
                        <span className="font-black text-slate-900 text-lg">‚Çπ{((userMode === 'wholesale' ? item.product.wholesalePrice : item.product.retailPrice) * item.quantity).toLocaleString()}</span>
                      </div>
                    </div>
                  </div>
                ))
              )}
            </div>

            <div className="p-8 border-t border-slate-100 bg-slate-50/50">
              <div className="space-y-3 mb-8">
                <div className="flex justify-between items-center text-sm font-bold text-slate-500 uppercase tracking-widest">
                  <span>Subtotal</span>
                  <span className="text-slate-900">‚Çπ{cartTotal.toLocaleString()}</span>
                </div>
                {userMode === 'wholesale' && (
                  <div className="flex justify-between items-center text-xs font-bold text-emerald-600 uppercase tracking-widest">
                    <span>GST (Integrated)</span>
                    <span>18% Applied</span>
                  </div>
                )}
                <div className="flex justify-between items-center pt-4 border-t border-slate-200">
                  <span className="text-slate-900 font-black text-xl tracking-tighter">Total Amount</span>
                  <span className="text-3xl font-black text-slate-900 tracking-tighter">‚Çπ{(cartTotal * (userMode === 'wholesale' ? 1.18 : 1)).toLocaleString()}</span>
                </div>
              </div>
              <button 
                disabled={cart.length === 0}
                className={`w-full py-6 rounded-[2rem] font-black text-white shadow-2xl transition-all disabled:opacity-50 disabled:cursor-not-allowed ${userMode === 'wholesale' ? 'bg-indigo-600 hover:bg-indigo-700 shadow-indigo-600/20' : 'bg-slate-900 hover:bg-slate-800 shadow-slate-900/20'}`}
              >
                {userMode === 'wholesale' ? 'Confirm Bulk Order' : 'Proceed to Checkout'}
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Modern Footer */}
      <footer className="bg-slate-900 text-white pt-32 pb-16">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-20 mb-32">
